            				Console.WriteLine("AVSADDR = " + TrxnResponse.AVSAddr);
            				Console.WriteLine("AVSZIP = " + TrxnResponse.AVSZip);
            				Console.WriteLine("IAVS = " + TrxnResponse.IAVS);
            			}
            			// Get the Fraud Response parameters.
